import React, { useState } from 'react';
import { Formik, Form, Field } from 'formik';
import * as yup from 'yup';
import './tripsform.css'; // Adjust to your CSS file
import 'bootstrap/dist/css/bootstrap.min.css';


const TripsForm = () => {
    const [formState, setFormState] = useState('submit'); // 'submit', 'edit', or 'cancel'

    const schema = yup.object().shape({
        destination: yup.string().required('Destination is required'),
        startDate: yup.date().required('Start date is required'),
        endDate: yup.date().required('End date is required'),
        purpose: yup.string().required('Purpose is required'),
        notes: yup.string(),
        media: yup.mixed()
    });

    const handleSubmit = (values) => {
        console.log(values);
        // Add logic for form submission
    };

    return (
        <div className='trips-form-container'>
            <div className="header">
                <div className="text">Plan Your Trip</div>
                <div className="underline"></div>
            </div>
            <Formik
                initialValues={{
                    destination: '',
                    startDate: '',
                    endDate: '',
                    purpose: '',
                    notes: '',
                    media: null
                }}
                validationSchema={schema}
                onSubmit={handleSubmit}
            >
                {({ setFieldValue }) => (
                    <Form>
                        <div className="inputs">
                            <div className="input">
                                <img src={destination_icon} alt="Destination Icon" />
                                <Field name="destination" type="text" placeholder="Destination" className="form-control" />
                            </div>
                            <div className="input">
                                <img src={calendar_icon} alt="Calendar Icon" />
                                <Field name="startDate" type="date" className="form-control" />
                                <Field name="endDate" type="date" className="form-control" />
                            </div>
                            <div className="input">
                                <img src={note_icon} alt="Note Icon" />
                                <Field name="purpose" type="text" placeholder="Purpose" className="form-control" />
                            </div>
                            <div className="input">
                                <img src={note_icon} alt="Note Icon" />
                                <Field name="notes" as="textarea" placeholder="Notes" className="form-control" />
                            </div>
                            <div className="input">
                                <img src={camera_icon} alt="Camera Icon" />
                                <input id="file" name="media" type="file" onChange={(event) => {
                                    setFieldValue("media", event.currentTarget.files[0]);
                                }} className="form-control" />
                            </div>
                        </div>
                        <div className="submit-container">
                            <button type="submit" className="btn btn-primary submit" disabled={formState !== 'submit'}>Submit</button>
                            <button type="button" className="btn btn-secondary submit" onClick={() => setFormState('edit')}>Edit</button>
                            <button type="button" className="btn btn-danger submit" onClick={() => setFormState('cancel')}>Cancel</button>
                        </div>
                    </Form>
                )}
            </Formik>
        </div>
    );
}

export default TripsForm;
