import React from 'react';
import './loginsignup.css';
import 'bootstrap/dist/css/bootstrap.min.css';

import user_icon from '../assets/images/person.png';
import email_icon from '../assets/images/email.png';
import password_icon from '../assets/images/password.png';

const LoginSignup = () => {

    const[action,setAction] = useState("Sign Up");

    const handleSignUpClick = () => {
        setAction("Sign Up");
        // Additional logic for Sign Up
    };

    const handleLoginClick = () => {
        setAction("Login");
        // Additional logic for Login
    }

    return (
        <div className='container'>
            <div className="header">
                <div className="text"> {action}</div>
                <div className="underline"></div>
            </div>
            <div className="inputs">
                <div className="input">
                    <img src={user_icon} alt="User Icon" />
                    <input type="text" placeholder="Name" className="form-control" />
                </div>
                <div className="input">
                    <img src={email_icon} alt="Email Icon" />
                    <input type="email" placeholder="Email Address" className="form-control" />
                </div>
                <div className="input">
                    <img src={password_icon} alt="Password Icon" />
                    <input type="password" placeholder="Password" className="form-control" />
                </div>
            </div>
            <div className="forgot-password">
                Lost Password? <a href="#reset-password">Click Here!</a>
            </div>
            <div className="submit-container">
            <button className="btn btn-primary submit" onClick={handleSignUpClick}>Sign Up</button>
                <button className="btn btn-secondary submit" onClick={handleLoginClick}>Login</button>
            </div>
        </div>
    )
}

export default LoginSignup;
