import React, { useState } from 'react';
import { Formik, Form, Field } from 'formik';
import * as yup from 'yup';
import './trip.css'; // Adjust to your CSS file
import 'bootstrap/dist/css/bootstrap.min.css';
import AppHeader from './header'


const TripsForm = () => {
    const [formState, setFormState] = useState('submit'); // 'submit', 'edit', or 'cancel'

    const schema = yup.object().shape({
        destination: yup.string().required('Destination is required'),
        startDate: yup.date().required('Start date is required'),
        endDate: yup.date().required('End date is required'),
        purpose: yup.string().required('Purpose is required'),
        notes: yup.string(),
        media: yup.mixed()
    });


    const handleSubmit = (values) => {
        console.log(values);
        // Add logic for form submission
    };

    return (
        <>
        <AppHeader />
        <div className='trips-form-container'>
            <div className="trips-header">
                <div className="trips-text">Add Your Trip</div>
                <div className="underline"></div>
            </div>
            <Formik
                initialValues={{
                    destination: '',
                    startDate: '',
                    endDate: '',
                    purpose: '',
                    notes: '',
                    media: null
                }}
                validationSchema={schema}
                onSubmit={handleSubmit}
            >
                {({ setFieldValue }) => (
                    <Form>
                        <div className="inputs">
                            <div className="input">
                                <Field name="destination" type="text" placeholder="Destination" className="form-control" />
                            </div>
                            <div className="input">
                                <Field name="TravelstartDate" type="date" className="form-control" />
                                <Field name="TravelendDate" type="date" className="form-control" />
                            </div>
                            <div className="input">
                                <Field name="purpose" type="text" placeholder="Purpose" className="form-control" />
                            </div>
                            <div className="input">
                                <Field name="notes" type="text" placeholder="Notes" className="form-control" />
                            </div>
                            <div className="input">
                                <input id="file" name="media" type="file" onChange={(event) => {
                                    setFieldValue("media", event.currentTarget.files[0]);
                                }} className="form-control" />
                            </div>
                        </div>
                        <div className="trips-submit-container">
                            <button type="submit" className="btn trip-btn-primary submit" disabled={formState !== 'submit'}>Submit</button>
                            <button type="submit" className="btn trip-btn-secondary submit" onClick={() => setFormState('edit')}>Edit</button>
                            <button type="submit" className="btn trip-btn-secondary submit" onClick={() => setFormState('cancel')}>Cancel</button>
                        </div>
                    </Form>
                )}
            </Formik>
        </div>
        </>
    );
}

export default TripsForm;
