import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import axios from 'axios';
import 'react-calendar/dist/Calendar.css'; // Ensure default calendar styles are imported
import AppHeader from '/Users/nicolemerriman/Desktop/wanderlogixs-app/src/components/header.js';
import './itinerary.css'; 

function Itinerary() {
    const [events, setEvents] = useState([]);
    const [eventForm, setEventForm] = useState({
        destination: '',
        date: new Date(),
        notes: ''
    });

    useEffect(() => {
        axios.get('http://localhost:3006/events')
            .then(response => setEvents(response.data))
            .catch(error => console.error('Error fetching events', error));
    }, []);

    const handleInputChange = (e) => {
        setEventForm({ ...eventForm, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Here you would handle adding the new event
        console.log('Event to add:', eventForm);
        // Reset form after submission
        setEventForm({ destination: '', date: new Date(), notes: '' });
    };

    return (
        <>
            <AppHeader />
            <div className='calendar-form-container'>
                <div className="calendar-header">
                    <div className="calendar-text">Your Itinerary</div>
                    <div className="underline"></div>
                </div>
                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        name="destination"
                        value={eventForm.destination}
                        onChange={handleInputChange}
                        placeholder="Destination"
                    />
                    <input
                        type="date"
                        name="date"
                        value={eventForm.date.toISOString().split('T')[0]}
                        onChange={handleInputChange}
                    />
                    <textarea
                        name="notes"
                        value={eventForm.notes}
                        onChange={handleInputChange}
                        placeholder="Notes"
                    />
                    <button type="submit">Add Event</button>
                </form>
                <div className="calendar-container">
                    <Calendar
                        onChange={(value) => setEventForm({ ...eventForm, date: value })}
                        value={eventForm.date}
                        className="react-calendar"
                        calendarType="US"
                    />
                </div>
            </div>
        </>
    );
}

export default Itinerary;
