import React, { useState } from 'react';
import axios from 'axios';

const EventPanel = ({ selectedDate, events, setEvents }) => {
  const [newEvent, setNewEvent] = useState({
    title: '',
    description: '',
    location: '',
    time: ''
  });

  const handleInputChange = (e) => {
    setNewEvent({ ...newEvent, [e.target.name]: e.target.value });
  };

  const addEvent = () => {
    const eventToAdd = {
      ...newEvent,
      date: selectedDate.toISOString().split('T')[0],
    };

    axios.post('http://localhost:3001/events', eventToAdd)
      .then(response => {
        setEvents([...events, response.data]);
        setNewEvent({ title: '', description: '', location: '', time: '' });
      })
      .catch(error => console.error('Error adding event', error));
  };

  const deleteEvent = (id) => {
    axios.delete(`http://localhost:3001/events/${id}`)
      .then(() => {
        setEvents(events.filter(event => event.id !== id));
      })
      .catch(error => console.error('Error deleting event', error));
  };

  const eventsOfSelectedDate = events.filter(
    event => new Date(event.date).toDateString() === selectedDate.toDateString()
  );

  return (
        <div className="event-panel">
          <div className="event-form-container"> {/* New container div */}
            <h3>Add New Event</h3>
        <input
          type="text"
          name="title"
          value={newEvent.title}
          onChange={handleInputChange}
          placeholder="Event Title"
        />
        <input
          type="text"
          name="description"
          value={newEvent.description}
          onChange={handleInputChange}
          placeholder="Description"
        />
        <input
          type="text"
          name="location"
          value={newEvent.location}
          onChange={handleInputChange}
          placeholder="Location"
        />
        <input
          type="time"
          name="time"
          value={newEvent.time}
          onChange={handleInputChange}
          placeholder="Time"
        />
        <button onClick={addEvent}>Add Event</button>
      </div>
      <ul>
        {eventsOfSelectedDate.map(event => (
          <li key={event.id}>
            <strong>{event.title}</strong> - {event.description} at {event.location} <span>{event.time}</span>
            <button onClick={() => deleteEvent(event.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EventPanel;
