function Itinerary() {
    const [events, setEvents] = useState([]);
    const [selectedDate, setSelectedDate] = useState(new Date());
    const [isModalVisible, setIsModalVisible] = useState(false);

    useEffect(() => {
        axios.get('http://localhost:3001/events')
            .then(response => setEvents(response.data))
            .catch(error => console.error('Error fetching events', error));
    }, []);

    const handleDayClick = (value) => {
        setSelectedDate(value);
        setIsModalVisible(true); // Show the modal on day click
    };

    const handleAddEvent = (eventDetails) => {
        // Here, you would handle submitting the new event data
        // This could involve updating state and/or making an API request
        console.log('Adding event:', eventDetails);

        // For now, let's just close the modal
        setIsModalVisible(false);
    };

    return (
        <>
         <AppHeader />
         <div className='calendar-form-container'> 
           <div className="calendar-header"> 
             <div className="calendar-text">Your Itinerary</div>
             <div className="underline"></div>
           </div>
           <div className="calendar-container"> 
             <Calendar
               onChange={handleDayClick}
               value={selectedDate}
               className="react-calendar"
               calendarType="US"
             />
           </div>
           <EventPanel 
             selectedDate={selectedDate} 
             events={events} 
             setEvents={setEvents} 
           />
           <Modal
             isVisible={isModalVisible}
             onClose={() => setIsModalVisible(false)}
             onAddEvent={handleAddEvent}
           />
         </div>
        </>
    );
}

export default Itinerary;
