import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Formik, Form, Field } from 'formik';
import * as yup from 'yup';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Card, Col, Row } from 'react-bootstrap';
import { Trash } from 'react-bootstrap-icons';

const schema = yup.object().shape({
  destination: yup.string().required('Destination is required'),
  startDate: yup.date().required('Start date is required'),
  endDate: yup.date().required('End date is required'),
  purpose: yup.string().required('Purpose is required'),
  notes: yup.string(),
  media: yup.mixed(),
});

function App() {
  const [trips, setTrips] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:3001/trips')
      .then((response) => {
        setTrips(response.data);
      })
      .catch((error) => {
        console.error('Error fetching trips:', error);
      });
  }, []);

  const handleSubmit = (values, { resetForm }) => {
    axios.post('http://localhost:3001/trips', values)
      .then((response) => {
        setTrips([...trips, response.data]);
        resetForm();
      })
      .catch((error) => {
        console.error('Error creating trip:', error);
      });
  };

  const handleDelete = (id) => {
    axios.delete(`http://localhost:3001/trips/${id}`)
      .then(() => {
        setTrips(trips.filter((trip) => trip.id !== id));
      })
      .catch((error) => {
        console.error('Error deleting trip:', error);
      });
  };

  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-md-6">
          <Formik
            initialValues={{
              destination: '',
              startDate: '',
              endDate: '',
              purpose: '',
              notes: '',
              media: null,
            }}
            validationSchema={schema}
            onSubmit={handleSubmit}
          >
            {({ setFieldValue }) => (
              <Form>
                {/* Form fields */}
                {/* ... (Form fields omitted for brevity) */}
                <button type="submit" className="btn btn-primary mt-3">
                  Submit
                </button>
              </Form>
            )}
          </Formik>
        </div>
        <div className="col-md-6">
          <h2>Trip List</h2>
          <Row xs={1} md={2} lg={3} className="g-4">
            {trips.map((trip) => (
              <Col key={trip.id}>
                <Card>
                  <Card.Img variant="top" src={URL.createObjectURL(trip.media)} alt="Trip" />
                  <Card.Body>
                    <Card.Title>{trip.destination}</Card.Title>
                    <Card.Text>
                      <b>Start Date:</b> {trip.startDate}
                    </Card.Text>
                    <Card.Text>
                      <b>End Date:</b> {trip.endDate}
                    </Card.Text>
                    <Card.Text>
                      <b>Purpose:</b> {trip.purpose}
                    </Card.Text>
                    <Card.Text>
                      <b>Notes:</b> {trip.notes}
                    </Card.Text>
                    <button
                      className="btn btn-danger"
                      onClick={() => handleDelete(trip.id)}
                    >
                      <Trash /> Delete
                    </button>
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>
        </div>
      </div>
    </div>
  );
}

export default Trip;
