import React, { useState } from 'react';
import './Modal.css'; 


const Modal = ({ isVisible, onClose, onAddEvent }) => {
  console.log('Modal render check, isVisible:', isVisible);
  const [eventDetails, setEventDetails] = useState({
    destination: '',
    time: '',
    notes: '',
    startDate: '',
    endDate: ''
  }
  );

  const handleInputChange = (e) => {
    setEventDetails({ ...eventDetails, [e.target.name]: e.target.value });
  };

  const handleSubmit = () => {
    onAddEvent(eventDetails);
    onClose();
  };

  const handleDayClick = (value) => {
    setSelectedDate(value);
    setIsModalVisible(true);
    console.log('Day clicked, modal should be visible'); // Debugging statement
};


  if (!isVisible) return null;

  return (
    <div>
      <div className="modal-backdrop" onClick={onClose}></div>
      <div className="modal">
        <div className="modal-close" onClick={onClose}>&times;</div>
        <div className="modal-header">Add New Event</div>
        <div className="modal-body">
          <input type="text" name="destination" placeholder="Destination" onChange={handleInputChange} />
          <input type="time" name="time" placeholder="Time" onChange={handleInputChange} />
          <textarea name="notes" placeholder="Notes" onChange={handleInputChange}></textarea>
          <input type="date" name="startDate" placeholder="Start Date" onChange={handleInputChange} />
          <input type="date" name="endDate" placeholder="End Date" onChange={handleInputChange} />
        </div>
        <div className="modal-footer">
          <button className="btn-modal" onClick={handleSubmit}>Add Event</button>
        </div>
      </div>
    </div>
  );
};

export default Modal;
