import React, { useState } from 'react';

const AppLogin = () => {
    const [isSignUp, setIsSignUp] = useState(true); // Toggle between Sign Up and Login
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');

    const handleSignUp = (event) => {
        event.preventDefault();
        // Basic validation
        if (email && password && password === confirmPassword) {
            console.log("Sign Up with", { email, password });
            // Implement sign-up logic here
        } else {
            alert("Invalid input for sign up");
        }
    };

    const handleLogin = (event) => {
        event.preventDefault();
        // Basic validation
        if (email && password) {
            console.log("Login with", { email, password });
            // Implement login logic here
        } else {
            alert("Invalid input for login");
        }
    };

    return (
        <div className='login-signup-container'>
            <div className="header">
                <div className="text">{isSignUp ? "Sign Up" : "Login"}</div>
                <div className="underline"></div>
            </div>
            <form onSubmit={isSignUp ? handleSignUp : handleLogin}>
                <div className="input">
                    <input 
                        type="email" 
                        placeholder="Email Address" 
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                </div>
                <div className="input">
                    <input 
                        type="password" 
                        placeholder="Password" 
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                </div>
                {isSignUp && (
                    <div className="input">
                        <input 
                            type="password" 
                            placeholder="Confirm Password" 
                            value={confirmPassword}
                            onChange={(e) => setConfirmPassword(e.target.value)}
                        />
                    </div>
                )}
                <div className="submit-container">
                    <button type="submit" className="submit">
                        {isSignUp ? "Sign Up" : "Login"}
                    </button>
                </div>
            </form>
            <div>
                {isSignUp ? 
                    <p>Already have an account? <button onClick={() => setIsSignUp(false)}>Login</button></p> :
                    <p>Don't have an account? <button onClick={() => setIsSignUp(true)}>Sign Up</button></p>
                }
            </div>
        </div>
    );
};

export default AppLogin;
