import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import * as yup from 'yup';
import AppHeader from '../components/header.js';
import './itinerary.css';

// Validation schema using Yup
const itinerarySchema = yup.object().shape({
    eventName: yup.string().required('Event name is required'),
    location: yup.string().required('Location is required'),
    startDate: yup.date().required('Start date is required').max(yup.ref('endDate'), 'Start date must be before end date'),
    endDate: yup.date().required('End date is required'),
    startTime: yup.string().required('Start time is required'),
    endTime: yup.string().required('End time is required'),
    description: yup.string(),
    notification: yup.string()
});

function Itinerary() {
    const [events, setEvents] = useState([]);
    const [eventForm, setEventForm] = useState({
        eventName: '',
        location: '',
        startDate: new Date(),
        endDate: new Date(),
        startTime: '12:00',
        endTime: '12:00',
        description: '',
        notification: ''
    });
    const [errors, setErrors] = useState({});

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        let updatedValue = value;

        if (name === 'startDate' || name === 'endDate') {
            updatedValue = new Date(value);
        }

        setEventForm({ ...eventForm, [name]: updatedValue });
    };

    const formatDateValue = (date) => {
        return date instanceof Date ? date.toISOString().split('T')[0] : '';
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await itinerarySchema.validate(eventForm, { abortEarly: false });
            setErrors({});
            setEvents(currentEvents => [
                ...currentEvents,
                {
                    ...eventForm,
                    id: Math.random().toString(36).substr(2, 9)
                }
            ]);

            // Reset form after submission
            setEventForm({
                eventName: '',
                location: '',
                startDate: new Date(),
                endDate: new Date(),
                startTime: '12:00',
                endTime: '12:00',
                description: '',
                notification: ''
            });
        } catch (err) {
            const validationErrors = err.inner.reduce((acc, error) => {
                acc[error.path] = error.message;
                return acc;
            }, {});
            setErrors(validationErrors);
        }
    };

    const handleCancel = () => {
        setEventForm({
            eventName: '',
            location: '',
            startDate: new Date(),
            endDate: new Date(),
            startTime: '12:00',
            endTime: '12:00',
            description: '',
            notification: ''
        });
        setErrors({});
    };

    return (
        <>
            <AppHeader />
            <div className='calendar-form-container'>
                <div className="calendar-header">
                <div className="calendar-text">Your Itinerary</div>
                <div className="underline"></div>
            </div>
                <form onSubmit={handleSubmit} className="itinerary-form">
                    {/* Event Name Input */}
                    <div className="form-row">
                        <div className="input">
                            <input
                                type="text"
                                name="eventName"
                                value={eventForm.eventName}
                                onChange={handleInputChange}
                                placeholder="Event Name"
                            />
                            {errors.eventName && <div className="error">{errors.eventName}</div>}
                        </div>

                        {/* Location Input */}
                        <div className="input">
                            <input
                                type="text"
                                name="location"
                                value={eventForm.location}
                                onChange={handleInputChange}
                                placeholder="Location"
                            />
                            {errors.location && <div className="error">{errors.location}</div>}
                        </div>
                    </div>

                    {/* Date and Time Inputs */}
                    <div className="form-row">
                        <div className="input">
                            <input
                                type="date"
                                name="startDate"
                                value={formatDateValue(eventForm.startDate)}
                                onChange={handleInputChange}
                                placeholder="Start Date"
                            />
                            {errors.startDate && <div className="error">{errors.startDate}</div>}
                        </div>
                        <div className="input">
                            <input
                                type="time"
                                name="startTime"
                                value={eventForm.startTime}
                                onChange={handleInputChange}
                                placeholder="Start Time"
                            />
                            {errors.startTime && <div className="error">{errors.startTime}</div>}
                        </div>
                    </div>

                    <div className="form-row">
                        <div className="input">
                            <input
                                type="date"
                                name="endDate"
                                value={formatDateValue(eventForm.endDate)}
                                onChange={handleInputChange}
                                placeholder="End Date"
                            />
                            {errors.endDate && <div className="error">{errors.endDate}</div>}
                        </div>
                        <div className="input">
                            <input
                                type="time"
                                name="endTime"
                                value={eventForm.endTime}
                                onChange={handleInputChange}
                                placeholder="End Time"
                            />
                            {errors.endTime && <div className="error">{errors.endTime}</div>}
                        </div>
                    </div>

                    {/* Description and Notification */}
                    <div className="form-row description-row">
                        <div className="input full-width">
                            <textarea
                                name="description"
                                value={eventForm.description}
                                onChange={handleInputChange}
                                placeholder="Description"
                            />
                            {errors.description && <div className="error">{errors.description}</div>}
                        </div>
                    </div>

                    <div className="form-row button-row">
                        <div className="input reminder-select">
                            <label>
                                Set Reminder:
                                <select
                                    name="notification"
                                    value={eventForm.notification}
                                    onChange={handleInputChange}>
                                    <option value="none">None</option>
                                    <option value="15min">15 minutes before</option>
                                    <option value="30min">30 minutes before</option>
                                    <option value="1hour">1 hour before</option>
                                    <option value="1day">1 day before</option>
                                </select>
                            </label>
                            {errors.notification && <div className="error">{errors.notification}</div>}
                        </div>

                        <div className="itinerary-submit-container">
                            <button type="submit" className="btn trip-btn-primary submit">Submit</button>
                            <button type="button" className="btn trip-btn-secondary submit" onClick={handleCancel}>Cancel</button>
                        </div>
                    </div>
                </form>

                {/* Calendar Component */}
                <div className="calendar-container">
                    <Calendar
                        onChange={(value) => setEventForm({ ...eventForm, startDate: value, endDate: value })}
                        value={eventForm.startDate}
                        className="react-calendar"
                        calendarType="US"
                        tileContent={({ date, view }) => view === 'month' && events.map((event) => {
                            const start = new Date(event.startDate);
                            const end = new Date(event.endDate);
                            if (date >= start && date <= end) {
                                return <div key={event.id} className="calendar-event">{event.eventName}</div>;
                            }
                            return null;
                        })}
                    />
                </div>
            </div>
        </>
    );
}

export default Itinerary;
