import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import axios from 'axios';
import EventPanel from '/Users/nicolemerriman/Desktop/wanderlogixs-app/src/components/eventpanel.js';
import 'react-calendar/dist/Calendar.css'; // Ensure default calendar styles are imported
import AppHeader from '/Users/nicolemerriman/Desktop/wanderlogixs-app/src/components/header.js';
import './itinerary.css'; 

function Itinerary() {
    const [events, setEvents] = useState([]);
    const [selectedDate, setSelectedDate] = useState(new Date());

    // Fetch events on component mount
    useEffect(() => {
        const fetchEvents = async () => {
            try {
                const response = await axios.get('http://localhost:3001/events');
                setEvents(response.data);
            } catch (error) {
                console.error('Error fetching events', error);
            }
        };

        fetchEvents();
    }, []);

    // Handle calendar day selection
    const handleDayClick = (value) => {
        setSelectedDate(value);
    };

    return (
        <>
            <AppHeader />
            <div className='calendar-form-container'> 
                <div className="calendar-header"> 
                    <div className="calendar-text">Your Itinerary</div>
                    <div className="underline"></div>
                </div>
                <div className="calendar-container"> 
                    <Calendar
                        onChange={handleDayClick}
                        value={selectedDate}
                        className="react-calendar"
                        calendarType="US" // Sets the calendar to start on Sunday
                    />
                </div>
                <EventPanel 
                    selectedDate={selectedDate} 
                    events={events} 
                    setEvents={setEvents} 
                />
            </div>
        </>
    );
}

export default Itinerary;
