import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import axios from 'axios';
import 'react-calendar/dist/Calendar.css';
import AppHeader from '/path/to/AppHeader'; // Update the path accordingly
import './itinerary.css';

function Itinerary() {
    const [events, setEvents] = useState([]);
    const [eventForm, setEventForm] = useState({
        destination: '',
        startDate: new Date(),
        endDate: new Date(),
        notes: ''
    });

    useEffect(() => {
        axios.get('http://localhost:3001/events')
            .then(response => setEvents(response.data))
            .catch(error => console.error('Error fetching events', error));
    }, []);

    const handleInputChange = (e) => {
        setEventForm({ ...eventForm, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Trip Details:', eventForm);
        // Add logic here to handle the form submission,
        // such as sending data to a backend server or updating state
    };

    return (
        <>
            <AppHeader />
            <div className='calendar-form-container'>
                <div className="calendar-header">
                    <div className="calendar-text">Your Itinerary</div>
                    <div className="underline"></div>
                </div>

                <form onSubmit={handleSubmit} className="trip-info-form">
                    <div className="input">
                        <label htmlFor="destination">Destination:</label>
                        <input
                            type="text"
                            id="destination"
                            name="destination"
                            value={eventForm.destination}
                            onChange={handleInputChange}
                            placeholder="Enter destination"
                        />
                    </div>
                    <div className="input">
                        <label htmlFor="startDate">Start Date:</label>
                        <input
                            type="date"
                            id="startDate"
                            name="startDate"
                            value={eventForm.startDate.toISOString().split('T')[0]}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div className="input">
                        <label htmlFor="endDate">End Date:</label>
                        <input
                            type="date"
                            id="endDate"
                            name="endDate"
                            value={eventForm.endDate.toISOString().split('T')[0]}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div className="input">
                        <label htmlFor="notes">Notes:</label>
                        <textarea
                            id="notes"
                            name="notes"
                            value={eventForm.notes}
                            onChange={handleInputChange}
                            placeholder="Enter any notes"
                        ></textarea>
                    </div>
                    <button type="submit" className="btn trip-btn-primary">Add Trip</button>
                </form>

                <div className="calendar-container">
                    <Calendar
                        onChange={(value) => setEventForm({ ...eventForm, startDate: value, endDate: value })}
                        value={eventForm.startDate}
                        className="react-calendar"
                        calendarType="US"
                    />
                </div>
            </div>
        </>
    );
}

export default Itinerary;
