import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import axios from 'axios';
import EventPanel from '/Users/nicolemerriman/Desktop/wanderlogixs-app/src/components/eventpanel.js';
import 'react-calendar/dist/Calendar.css'; // Make sure to import the default calendar styles
import AppHeader from ''

function Itinerary() {
    const [events, setEvents] = useState([]);
    const [selectedDate, setSelectedDate] = useState(new Date());

    useEffect(() => {
        axios.get('http://localhost:3001/events')
            .then(response => setEvents(response.data))
            .catch(error => console.error('Error fetching events', error));
    }, []);

    const handleDayClick = (value) => {
        setSelectedDate(value);
    };

    return (
        <div className="calendar-form-container"> {/* Apply the container style */}
            <div className="calendar-header"> {/* Apply the header style */}
                <div className="calendar-title">Your Itinerary</div>
                <div className="calendar-underline"></div>
            </div>
            <Calendar
                onChange={handleDayClick}
                value={selectedDate}
                className="react-calendar" // Apply the calendar styling
            />
            <EventPanel 
                selectedDate={selectedDate} 
                events={events} 
                setEvents={setEvents} 
            />
        </div>
    );
}

export default Itinerary;
