import React, { useState } from 'react';
import { Formik, Form, Field } from 'formik';
import * as yup from 'yup';
import './expenses.css'; // Adjust to your CSS file
import 'bootstrap/dist/css/bootstrap.min.css';
import AppHeader from './header'



    const schema = yup.object().shape({
        destination: yup.string().required('Destination is required'),
        startDate: yup.date().required('Start date is required'),
        endDate: yup.date().required('End date is required'),
        purpose: yup.string().required('Purpose is required'),
        notes: yup.string(),
        media: yup.mixed()
    });

    const ExpenseCard = ({ trip }) => {
        return (
            <div className="expense-card">
                <div className="expense-info">
                    <h3>{expense.destination}</h3>
                    <p><b>Start Date:</b> {trip.startDate}</p>
                    <p><b>End Date:</b> {trip.endDate}</p>
                    <p><b>Purpose:</b> {trip.purpose}</p>
                    <p><b>Notes:</b> {trip.notes}</p>
                </div>
                {expense.media && (
                    <div className="expensemedia">
                        {/* Displaying the image if media is available */}
                        <img src={URL.createObjectURL(Expense.media)} alt="Expense" />
                    </div>
                )}
            </div>
        );
    };

    const ExpenseForm = () => {
        const [expense, setExpense] = useState([]);
        const [formState, setFormState] = useState('submit');

   const handleSubmit = (values, { resetForm }) => {
    if (formState === 'submit') {
        setExpense([values, ...expense]);
    }
    resetForm();
    setFormState('submit'); 
};

const handleEdit = () => {
    setFormState('edit');
};

const handleCancel = () => {
    setFormState('cancel');
    document.getElementById('expense-form').reset(); 
};


    return (
        <>
            <AppHeader />
            <div className='expense-form-container'>
                <div className="expense-header">
                    <div className="expense-text">Add Your Expenses</div>
                    <div className="underline"></div>
                </div>
                <Formik
                    initialValues={{
                        destination: '',
                        startDate: '',
                        endDate: '',
                        purpose: '',
                        notes: '',
                        media: null
                    }}
                    validationSchema={schema}
                    onSubmit={handleSubmit}
                >
                    {({ setFieldValue }) => (
                        <Form>
                            <div className="inputs">
                                {/* Form fields */}
                                <div className="input">
                                    <Field name="destination" type="text" placeholder="Destination" className="form-control" />
                                </div>
                                <div className="input">
                                    <Field name="startDate" type="date" className="form-control" />
                                    <Field name="endDate" type="date" className="form-control" />
                                </div>
                                <div className="input">
                                    <Field name="purpose" type="text" placeholder="Purpose" className="form-control" />
                                </div>
                                <div className="input">
                                    <Field name="notes" type="text" placeholder="Notes" className="form-control" />
                                </div>
                                <div className="input">
                                    <input id="file" name="media" type="file" onChange={(event) => {
                                        setFieldValue("media", event.currentTarget.files[0]);
                                    }} className="form-control" />
                                </div>
                            </div>
                            <div className="trips-submit-container">
                            <button type="submit" className="btn expense-btn-primary submit" disabled={formState !== 'submit'}>Submit</button>
                            <button type="button" className="btn expense-btn-secondary submit" onClick={handleEdit}>Edit</button>
                            <button type="button" className="btn expense-btn-secondary submit" onClick={handleCancel}>Cancel</button>
                             </div>
                        </Form>
                    )}
                </Formik>
            </div>
            <div className="expense-display">
                {expense.map((expense, index) => (
                    <ExpenseCard key={index} expense={expense} />
                ))}
            </div>
        </>
    );
}

export default AppExpenses;
