import React, { useState } from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker'; // Assuming you are using react-datepicker
import 'react-datepicker/dist/react-datepicker.css';

const EventPanel = ({ selectedDate, events, setEvents }) => {
  const [newEvent, setNewEvent] = useState({
    title: '',
    description: '',
    location: '',
    startTime: '',
    endTime: '',
    startDate: selectedDate,
    endDate: selectedDate
  });

  const handleInputChange = (e) => {
    setNewEvent({ ...newEvent, [e.target.name]: e.target.value });
  };

  const handleDateChange = (dates) => {
    const [start, end] = dates;
    setNewEvent({ ...newEvent, startDate: start, endDate: end });
  };

  const addEvent = () => {
    const eventToAdd = {
      ...newEvent,
      startDate: newEvent.startDate.toISOString().split('T')[0],
      endDate: newEvent.endDate.toISOString().split('T')[0],
    };

    axios.post('http://localhost:3001/events', eventToAdd)
      .then(response => {
        setEvents([...events, response.data]);
        setNewEvent({ title: '', description: '', location: '', startTime: '', endTime: '', startDate: selectedDate, endDate: selectedDate });
      })
      .catch(error => console.error('Error adding event', error));
  };

  const deleteEvent = (id) => {
    axios.delete(`http://localhost:3001/events/${id}`)
      .then(() => {
        setEvents(events.filter(event => event.id !== id));
      })
      .catch(error => console.error('Error deleting event', error));
  };

  // Filtering logic may need to be updated depending on how you want to display multi-day events
  const eventsOfSelectedDate = events.filter(
    event => new Date(event.startDate).toDateString() === selectedDate.toDateString()
  );

  return (
    <div className="event-panel">
      <div className="event-form-container">
        <h3>Add New Event</h3>
        <input
          type="text"
          name="title"
          value={newEvent.title}
          onChange={handleInputChange}
          placeholder="Event Title"
        />
        {/* ... other inputs ... */}
        <DatePicker
          selected={newEvent.startDate}
          onChange={handleDateChange}
          startDate={newEvent.startDate}
          endDate={newEvent.endDate}
          selectsRange
          inline
        />
        <button onClick={addEvent}>Add Event</button>
      </div>
      <ul>
        {/* ... event list ... */}
      </ul>
    </div>
  );
};

export default EventPanel;
