import React, { useState } from 'react';
import { Formik, Form, Field, FieldArray } from 'formik';
import * as yup from 'yup';
import './media.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import AppHeader from './header';
import Card from 'react-bootstrap/Card';
import { PlusCircle } from 'react-bootstrap-icons';

const schema = yup.object().shape({
  category: yup.string().required('Category is required'),
  tags: yup.array().of(yup.string()),
  notes: yup.string(),
  media: yup.array(),
});

const MediaCard = ({ expense }) => {
  return (
    <Card style={{ width: '18rem' }}>
      <Card.Body>
        <Card.Title>Card Title</Card.Title>
        <Card.Subtitle className="mb-2 text-muted">Card Subtitle</Card.Subtitle>
        <Card.Text>
          Some quick example text to build on the card title and make up the
          bulk of the card's content.
        </Card.Text>
      </Card.Body>
    </Card>
  );
};

const AppMedia = () => {
  const [expenses, setExpenses] = useState([]);
  const [formState, setFormState] = useState('submit');

  const handleSubmit = (values, { resetForm }) => {
    if (formState === 'submit') {
      setExpenses([values, ...expenses]);
      resetForm();
      setFormState('submitted');

      // Reset the form state back to 'submit' after a delay
      setTimeout(() => {
        setFormState('submit');
      }, 1); // Change the delay as needed
    }
  };

  const handleEdit = () => {
    setFormState('edit');
  };

  const handleCancel = () => {
    setFormState('cancel');
    document.getElementById('expense-form').reset();
  };

  return (
    <>
      <AppHeader />
      <div className="expense-form-container">
        <div className="expense-header">
          <div className="expense-text">Upload Your Media</div>
          <div className="underline"></div>
        </div>
        <Formik
          initialValues={{
            category: '',
            tags: [],
            notes: '',
            media: [],
          }}
          validationSchema={schema}
          onSubmit={handleSubmit}
        >
          {({ setFieldValue }) => (
            <Form id="expense-form">
              <div className="inputs">
                <div className="input">
                  <Field as="select" name="tripcategory" className="form-control">
                    <option value="">Select Trip Category</option>
                    <option value="business">Business</option>
                    <option value="vacation">Leisure</option>
                    <option value="family">Adventure</option>
                    <option value="family">Cultural</option>
                    <option value="family">Family</option>
                    <option value="family">Family</option>


                  </Field>
                </div>
                <div className="input">
                <FieldArray
                    name="tags"
                    render={({ push, remove, form }) => (
                        <div>
                        {form.values.tags.map((tag, index) => (
                            <div key={index}>
                            <Field
                                name={`tags[${index}]`}
                                type="text"
                                placeholder="Tag"
                                className="form-control"
                            />
                            <button type="button" onClick={() => remove(index)}>
                                Remove
                            </button>
                            </div>
                        ))}
                        <button type="button" onClick={() => push('')}>
                            <PlusCircle /> Add Tag
                        </button>
                        </div>
                    )}
                    />
                </div>
                <div className="input">
                  <Field
                    name="notes"
                    as="textarea"
                    placeholder="Notes"
                    className="form-control"
                  />
                </div>
                <div className="input">
                  <input
                    id="file"
                    name="media"
                    type="file"
                    multiple
                    onChange={(event) => {
                      setFieldValue('media', event.currentTarget.files);
                    }}
                    className="form-control"
                  />
                </div>
              </div>
              <div className="expense-submit-container">
                <button
                  type="submit"
                  className="btn expense-btn-primary submit"
                  disabled={formState !== 'submit'}
                >
                  {formState === 'edit' ? 'Save' : 'Submit'}
                </button>
                <button
                  type="button"
                  className="btn expense-btn-secondary submit"
                  onClick={handleEdit}
                  disabled={formState === 'edit'}
                >
                  Edit
                </button>
                <button
                  type="button"
                  className="btn expense-btn-secondary submit"
                  onClick={handleCancel}
                  disabled={formState === 'edit'}
                >
                  Cancel
                </button>
              </div>
            </Form>
          )}
        </Formik>
      </div>
      <div className="expense-display">
        {expenses.map((expense, index) => (
          <MediaCard key={index} expense={expense} />
        ))}
      </div>
    </>
  );
};

export default AppMedia;
