import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import axios from 'axios';
import EventPanel from '/Users/nicolemerriman/Desktop/wanderlogixs-app/src/components/eventpanel.js';
import 'react-calendar/dist/Calendar.css'; // Ensure default calendar styles are imported
import AppHeader from '/Users/nicolemerriman/Desktop/wanderlogixs-app/src/components/header.js'

function Itinerary() {
    const [events, setEvents] = useState([]);
    const [selectedDate, setSelectedDate] = useState(new Date());

    useEffect(() => {
        axios.get('http://localhost:3001/events')
            .then(response => setEvents(response.data))
            .catch(error => console.error('Error fetching events', error));
    }, []);

    const handleDayClick = (value) => {
        setSelectedDate(value);
    };

    return (
        <>
         <AppHeader />
         <div className='itinerary-form-container'> 
           <div className="itinerary-header"> 
             <div className="trips-text">Your Itinerary</div>
             <div className="underline"></div>
           </div>
           <div className="calendar-container"> {/* Custom class for the calendar */}
             <Calendar
               onChange={handleDayClick}
               value={selectedDate}
               className="react-calendar" // Apply the default calendar class
             />
           </div>
           <EventPanel 
             selectedDate={selectedDate} 
             events={events} 
             setEvents={setEvents} 
           />
         </div>
        </>
    );
}

export default Itinerary;
