import React from 'react';
import './Modal.css'; 

const Modal = ({ isVisible, onClose, onAddEvent }) => {
  if (!isVisible) return null;

  const handleSubmit = () => {
    const eventDetails = {
      destination: 'Test Destination',
      time: '12:00',
      notes: 'Test notes',
      startDate: '2023-01-01',
      endDate: '2023-01-02'
    };
    onAddEvent(eventDetails);
    onClose();
  };

  return (
    <div>
      <div className="modal-backdrop" onClick={onClose}></div>
      <div className="modal">
        <div className="modal-close" onClick={onClose}>&times;</div>
        <div className="modal-header">Add New Event</div>
        <div className="modal-body">
          <p>Event details form goes here</p>
        </div>
        <div className="modal-footer">
          <button className="btn-modal" onClick={handleSubmit}>Add Event</button>
        </div>
      </div>
    </div>
  );
};

export default Modal;
