import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import './itinerary.css'; 

function Itinerary() {
    const [events, setEvents] = useState([]);
    const [eventForm, setEventForm] = useState({
        destination: '',
        startDate: new Date(),
        startTime: '12:00',
        endDate: new Date(),
        endTime: '12:00',
        notes: ''
    });
    const [formState, setFormState] = useState('submit'); // Added formState

    const handleInputChange = (e) => {
        setEventForm({ ...eventForm, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (formState === 'submit') {
            const newEvent = {
                ...eventForm,
                id: Date.now(),
                startDate: new Date(eventForm.startDate + 'T' + eventForm.startTime),
                endDate: new Date(eventForm.endDate + 'T' + eventForm.endTime)
            };
            setEvents([...events, newEvent]);
            setEventForm({ destination: '', startDate: new Date(), startTime: '12:00', endDate: new Date(), endTime: '12:00', notes: '' });
        }
    };

    const handleCancel = () => {
        setEventForm({ destination: '', startDate: new Date(), startTime: '12:00', endDate: new Date(), endTime: '12:00', notes: '' });
        setFormState('submit'); // Reset the form state to 'submit'
    };

    return (

            <div className='calendar-form-container'>
                <div className="calendar-header">
                    <div className="calendar-text">Your Itinerary</div>
                    <div className="underline"></div>
                </div>
                <form onSubmit={handleSubmit} className="itinerary-form">
                    <div className="form-row">
                        <div className="input">
                            <input
                                type="text"
                                name="eventName"
                                value={eventForm.eventName}
                                onChange={handleInputChange}
                                placeholder="Event Name"
                            />
                        </div>
                        <div className="input">
                            <input
                                type="text"
                                name="location"
                                value={eventForm.location}
                                onChange={handleInputChange}
                                placeholder="Location"
                            />
                        </div>
                    </div>
                    <div className="form-row">
                        <div className="input">
                            <input
                                type="date"
                                name="startDate"
                                value={eventForm.startDate.toISOString().split('T')[0]}
                                onChange={handleInputChange}
                                placeholder="Start Date"
                            />
                        </div>
                        <div className="input">
                            <input
                                type="time"
                                name="startTime"
                                value={eventForm.startTime}
                                onChange={handleInputChange}
                                placeholder="Start Time"
                            />
                        </div>
                    </div>
                    <div className="form-row">
                        <div className="input">
                            <input
                                type="date"
                                name="endDate"
                                value={eventForm.endDate.toISOString().split('T')[0]}
                                onChange={handleInputChange}
                                placeholder="End Date"
                            />
                        </div>
                        <div className="input">
                            <input
                                type="time"
                                name="endTime"
                                value={eventForm.endTime}
                                onChange={handleInputChange}
                                placeholder="End Time"
                            />
                        </div>
                    </div>
                    <div className="form-row description-row">
                        <div className="input full-width">
                            <textarea
                                name="description"
                                value={eventForm.description}
                                onChange={handleInputChange}
                                placeholder="Description"
                            />
                        </div>
                    </div>
                    <div className="form-row button-row">
                        <div className="input reminder-select">
                            <label>
                                Set Reminder:
                                <select
                                    name="notification"
                                    value={eventForm.notification}
                                    onChange={handleInputChange}>
                                    <option value="none">None</option>
                                    <option value="15min">15 minutes before</option>
                                    <option value="30min">30 minutes before</option>
                                    <option value="1hour">1 hour before</option>
                                    <option value="1day">1 day before</option>
                                </select>
                            </label>
                        </div>
                        <div className="itinerary-submit-container">
                            <button type="submit" className="btn trip-btn-primary submit">Submit</button>
                            <button type="button" className="btn trip-btn-secondary submit" onClick={handleCancel}>Cancel</button>
                        </div>
                    </div>

                </form>
                <div className="calendar-container">
                    <Calendar
                        onChange={(value) => setEventForm({ ...eventForm, startDate: value, endDate: value })}
                        value={eventForm.startDate}
                        className="react-calendar"
                        calendarType="US"
                        tileContent={({ date, view }) => view === 'month' && events.map((event) => {
                            const start = new Date(event.startDate);
                            const end = new Date(event.endDate);
                            if (date >= start && date <= end) {
                                return <div key={event.id} className="calendar-event">{event.destination}</div>;
                            }
                            return null;
                        })}
                    />
                </div>
            </div>
    );
}

export default Itinerary;
