import React, { useState } from 'react';
import { Formik, Form, Field } from 'formik';
import * as yup from 'yup';
import './trip.css'; // Adjust to your CSS file
import 'bootstrap/dist/css/bootstrap.min.css';
import AppHeader from './header';
import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup';

const schema = yup.object().shape({
  destination: yup.string().required('Destination is required'),
  startDate: yup.date().required('Start date is required'),
  endDate: yup.date().required('End date is required'),
  purpose: yup.string().required('Purpose is required'),
  notes: yup.string(),
  media: yup.mixed(),
});

const TripCard = ({ trip }) => {
  return (
    <Card style={{ width: '18rem' }}>
      {trip.media && (
        <Card.Img variant="top" src={URL.createObjectURL(trip.media)} alt="Trip" />
      )}
      <Card.Body>
        <Card.Title>{trip.destination}</Card.Title>
        <Card.Text>
          <b>Start Date:</b> {trip.startDate}
        </Card.Text>
        <Card.Text>
          <b>End Date:</b> {trip.endDate}
        </Card.Text>
        <Card.Text>
          <b>Purpose:</b> {trip.purpose}
        </Card.Text>
        <Card.Text>
          <b>Notes:</b> {trip.notes}
        </Card.Text>
      </Card.Body>
    </Card>
  );
};

const TripsForm = () => {
  const [trips, setTrips] = useState([]);
  const [formState, setFormState] = useState('submit');

  const handleSubmit = (values, { resetForm }) => {
    if (formState === 'submit') {
      setTrips([values, ...trips]);
    }
    resetForm();
    setFormState('submit');
  };

  const handleEdit = () => {
    setFormState('edit');
  };

  const handleCancel = () => {
    setFormState('cancel');
    document.getElementById('trip-form').reset();
  };

  return (
    <>
      <AppHeader />
      <div className='trips-form-container'>
        <div className="trips-header">
          <div className="trips-text">Add Your Trip</div>
          <div className="underline"></div>
        </div>
        <Formik
          initialValues={{
            destination: '',
            startDate: '',
            endDate: '',
            purpose: '',
            notes: '',
            media: null,
          }}
          validationSchema={schema}
          onSubmit={handleSubmit}
        >
          {({ setFieldValue }) => (
            <Form>
              <div className="inputs">
                {/* Form fields */}
                <div className="input">
                  <Field name="destination" type="text" placeholder="Destination" className="form-control" />
                </div>
                <div className="input">
                  <Field name="startDate" type="date" className="form-control" />
                  <Field name="endDate" type="date" className="form-control" />
                </div>
                <div className="input">
                  <Field name="purpose" type="text" placeholder="Purpose" className="form-control" />
                </div>
                <div className="input">
                  <Field name="notes" type="text" placeholder="Notes" className="form-control" />
                </div>
                <div className="input">
                  <input
                    id="file"
                    name="media"
                    type="file"
                    onChange={(event) => {
                      setFieldValue('media', event.currentTarget.files[0]);
                    }}
                    className="form-control"
                  />
                </div>
              </div>
              <div className="trips-submit-container">
                <button type="submit" className="btn trip-btn-primary submit" disabled={formState !== 'submit'}>
                  Submit
                </button>
                <button type="button" className="btn trip-btn-secondary submit" onClick={handleEdit}>
                  Edit
                </button>
                <button type="button" className="btn trip-btn-secondary submit" onClick={handleCancel}>
                  Cancel
                </button>
              </div>
            </Form>
          )}
        </Formik>
      </div>
      <div className="trips-display">
        {trips.map((trip, index) => (
          <TripCard key={index} trip={trip} />
        ))}
      </div>
    </>
  );
};

export default TripsForm;
