import React, { useState } from 'react';
import './trips.css'; // Ensure you have the CSS file for styling
import 'bootstrap/dist/css/bootstrap.min.css';

//import destination_icon from '../assets/images/destination.png';
//import date_icon from '../assets/images/date.png';
//import details_icon from '../assets/images/details.png';

const TripsForm = () => {
    const [tripData, setTripData] = useState({
        destination: '',
        date: '',
        details: ''
    });

    const handleChange = (e) => {
        setTripData({
            ...tripData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Trip Data:', tripData);
        // Here you can add logic to send this data to a server or process it further
    };

    return (
        <div className='trips-form-container'>
            <div className="header">
                <div className="text">Plan Your Trip</div>
                <div className="underline"></div>
            </div>
            <form onSubmit={handleSubmit}>
                <div className="inputs">
                    <div className="input">
                        <img src={destination_icon} alt="Destination Icon" />
                        <input
                            type="text"
                            placeholder="Destination"
                            className="form-control"
                            name="destination"
                            value={tripData.destination}
                            onChange={handleChange}
                        />
                    </div>
                    <div className="input">
                        <img src={date_icon} alt="Date Icon" />
                        <input
                            type="date"
                            className="form-control"
                            name="date"
                            value={tripData.date}
                            onChange={handleChange}
                        />
                    </div>
                    <div className="input">
                        <img src={details_icon} alt="Details Icon" />
                        <textarea
                            placeholder="Trip Details"
                            className="form-control"
                            name="details"
                            value={tripData.details}
                            onChange={handleChange}
                        />
                    </div>
                </div>
                <div className="submit-container">
                    <button type="submit" className="btn btn-primary submit">Submit</button>
                </div>
            </form>
        </div>
    );
}

export default TripsForm;
