import React, { useState } from 'react';
import { Formik, Form, Field, FieldArray } from 'formik';
import * as yup from 'yup';
import './media.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import AppHeader from './header';
import Card from 'react-bootstrap/Card';
import { PlusCircle } from 'react-bootstrap-icons';

const schema = yup.object().shape({
  category: yup.string().required('Category is required'),
  tags: yup.string(),
  notes: yup.string(),
  media: yup.array(),
});

const MediaCard = ({ expense }) => {
  return (
    <Card style={{ width: '18rem' }}>
      <Card.Body>
        <Card.Title>Card Title</Card.Title>
        <Card.Subtitle className="mb-2 text-muted">Card Subtitle</Card.Subtitle>
        <Card.Text>
          Some quick example text to build on the card title and make up the
          bulk of the card's content.
        </Card.Text>
      </Card.Body>
    </Card>
  );
};

const AppMedia = () => {
    const [mediaEntries, setMediaEntries] = useState([]);
    const [formState, setFormState] = useState('submit');

    const handleSubmit = (values, { resetForm }) => {
      if (formState === 'submit') {
        // Process tags to be an array if needed
        const processedTags = values.tags.split(',').map(tag => tag.trim());
        const newEntry = { ...values, tags: processedTags };

        setMediaEntries([newEntry, ...mediaEntries]);
        resetForm();
        setFormState('submit');

      // Reset the form state back to 'submit' after a delay
      setTimeout(() => {
        setFormState('submit');
      }, 1); // Change the delay as needed
    }
  };

  const handleEdit = () => {
    setFormState('edit');
  };

  const handleCancel = () => {
    setFormState('cancel');
    document.getElementById('expense-form').reset();
  };

  return (
    <>
      <AppHeader />
      <div className="media-form-container">
        <div className="media-header">
          <div className="media-text">Upload Your Media</div>
          <div className="underline"></div>
        </div>
        <Formik
            initialValues={{ category: '', tags: '', notes: '', media: [] }}
            validationSchema={schema}
            onSubmit={handleSubmit}
        >
          {({ setFieldValue }) => (
            <Form id="media-form">
              <div className="inputs">
                <div className="input">
                  <Field as="select" name="tripcategory" className="form-control">
                    <option value="">Select Trip Category</option>
                    <option value="business">Business</option>
                    <option value="leisure">Leisure</option>
                    <option value="adventure">Adventure</option>
                    <option value="cultural">Cultural</option>
                    <option value="family">Family</option>
                    <option value="romantic">Romantic</option>
                  </Field>
                </div>
                <div className="input">
                    <FieldArray
                        name="tags"
                        render={({ push, remove, form }) => (
                            <div>
                                {form.values.tags.length < 10 && (
                                    <button 
                                        type="button" 
                                        className="add-tag-btn" 
                                        onClick={() => push('')}>
                                        <PlusCircle /> Add Tag
                                    </button>
                                )}

                                {form.values.tags.map((tag, index) => (
                                    <div key={index} className="tag-item">
                                        <Field
                                            name={`tags[${index}]`}
                                            type="text"
                                            placeholder="Tag"
                                            className="form-control tag-input"
                                        />
                                        <button 
                                            type="button" 
                                            className="remove-tag-btn" 
                                            onClick={() => remove(index)}>
                                            Remove
                                        </button>
                                    </div>
                                ))}
                            </div>
                        )}
                    />
                </div>

                <div className="input">
                  <Field
                    name="notes"
                    type="text"
                    placeholder="Notes"
                    className="form-control"
                  />
                </div>
                <div className="input">
                  <input
                    id="file"
                    name="media"
                    type="file"
                    multiple
                    onChange={(event) => {
                      setFieldValue('media', event.currentTarget.files);
                    }}
                    className="form-control"
                  />
                </div>
              </div>
              <div className="media-submit-container">
                <button
                  type="submit"
                  className="btn media-btn-primary submit"
                  disabled={formState !== 'submit'}
                >
                  {formState === 'edit' ? 'Save' : 'Submit'}
                </button>
                <button
                  type="button"
                  className="btn media-btn-secondary submit"
                  onClick={handleEdit}
                  disabled={formState === 'edit'}
                >
                  Edit
                </button>
                <button
                  type="button"
                  className="btn media-btn-secondary submit"
                  onClick={handleCancel}
                  disabled={formState === 'edit'}
                >
                  Cancel
                </button>
              </div>
            </Form>
          )}
        </Formik>
      </div>
      <div className="media-display">
        {media.map((media, index) => (
          <MediaCard key={index} media={media} />
        ))}
      </div>
    </>
  );
};

export default AppMedia;
